import React, { useEffect, useState } from 'react'
import Navbar from '../components/Navbar/NavBar'
import "./mainpage.css"
import PersonalInfo from '../components/formComponents/PersonalInfo'
import FavouriteBooks from '../components/formComponents/FavouriteBooks'
import Author from '../components/formComponents/Author'
import Alluserdetail from '../components/formComponents/Alluserdetail'
import { userDataAction } from '../redux/action'
import { useDispatch } from 'react-redux'
import { useSelector } from 'react-redux'
import Saved from '../components/formComponents/saved'



const validfname=new RegExp('^[A-Za-z]{3,15}$')
const validemail=new RegExp('^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$')
export default function Mainpage() {
const Alldata=useSelector((state)=>state.userdata)
const [error,seterror]=useState(false)
const [errormsg,seterrormsg]=useState({
        fname:"",
        lname:"",
        email:"",
        sex:"",
        phone:"",
        fbook:"",
        sbook:"",
        tbook:"",
        author:"",
    })

const dispatch=useDispatch()


    const [step,setstep]=useState(1)
    const [userdetail,setuserdetail]=useState({
        fname:"",
        lname:"",
        email:"",
        sex:"Select Gender",
        phone:"",
        fbook:"",
        sbook:"",
        tbook:"",
        author:"Select Author",
    })
    const Nextfun=()=>{
        if(step<5){
            setstep(step + 1)
        }
 
    }
    const Prevfun=()=>{
        if(step>1){
            setstep(step - 1)
        }}
    const savefun=()=>{
dispatch(userDataAction(userdetail))
setstep(5)
setuserdetail({
        fname:"",
        lname:"",
        email:"",
        sex:"Select Gender",
        phone:"",
        fbook:"",
        sbook:"",
        tbook:"",
        author:"Select Author",
    })
    }
    const handleInput=(e)=>{
        const name=e.target.name
        if(name==="fbook"||name==="tbook"||name==="tbook"){
            const value=e.target.value
            setuserdetail({...userdetail,[name]:value})
        }else{
            const value=e.target.value.trim()
            setuserdetail({...userdetail,[name]:value})
        }

        if(step===1){
            if(name==="fname"){
                if(!validfname.test(e.target.value.trim())){
                    seterror(true)
                    seterrormsg({...errormsg,fname:"Must be at least 3 character long."})
                }
            }
            else if(name==="lname"){
                if(!validfname.test(e.target.value.trim())){
                    seterror(true)
                    seterrormsg({...errormsg,lname:"Must be at least 3 character long."})
                }
            }
            else if(name==="email"){
                if(!validfname.test(e.target.value.trim())){
                    seterror(true)
                    seterrormsg({...errormsg,email:"enter a valid email"})
                }
            }
        }
        console.log(errormsg)
    }
    
    console.log(Alldata.Data)
  return (<>
  <Navbar/>
    <div className="MainpageWrapper">
    <div className="innerwrapper">
<div className="formwrapper">
    <div className="upperform">
    {step===1?<PersonalInfo userdetail={userdetail} setuserdetail={setuserdetail} handleInput={handleInput}
    />:step===2?<FavouriteBooks userdetail={userdetail} setuserdetail={setuserdetail} handleInput={handleInput}/>:step===3?<Author userdetail={userdetail} setuserdetail={setuserdetail}handleInput={handleInput} />:step===4?<Alluserdetail userdetail={userdetail} setuserdetail={setuserdetail} handleInput={handleInput}/>:<Saved/>}
    </div>
    <div className="bottomform">
    <div className="pagecount">
        {step!==5?<ul className="countlist">
            <li className={step===1?"active":null} >1</li>
            <li className={step===2?"active":null} >2</li>
            <li className={step===3?"active":null} >3</li>
            <li className={step===4?"active":null} >4</li>
        </ul>:null}
    </div>
    <div className="bottombutton">
        {step===5?<button className="prev btn" disabled={step===1?"disabled":null} onClick={()=>{setstep(1)}}>Home</button>:<><button className="prev btn" disabled={step===1?"disabled":null} onClick={Prevfun}>Previous</button><button className="next btn" onClick={step===4?savefun:Nextfun}>{step===4?"Confirm & Save":"Next"}</button></>}
        
    </div>
    </div>
</div>
    </div>
    </div>
  </>

  )
}
