import React, { useEffect, useState } from 'react'
import Navbar from '../components/Navbar/NavBar'
import "./mainpage.css"
import PersonalInfo from '../components/formComponents/PersonalInfo'
import FavouriteBooks from '../components/formComponents/FavouriteBooks'
import Author from '../components/formComponents/Author'
import Alluserdetail from '../components/formComponents/Alluserdetail'
import { userDataAction } from '../redux/action'
import { useDispatch } from 'react-redux'
import { useSelector } from 'react-redux'




export default function Mainpage() {
const Alldata=useSelector((state)=>state.userdata)
const dispatch=useDispatch()


    const [step,setstep]=useState(1)
    const [userdetail,setuserdetail]=useState({
        fname:"",
        lname:"",
        email:"",
        sex:"Select Gender",
        phone:"",
        fbook:"",
        sbook:"",
        tbook:"",
        author:"Select Author",
    })
    const Nextfun=()=>{
        if(step<4){
            setstep(step + 1)
        }
    }
    const Prevfun=()=>{
        if(step>1){
            setstep(step - 1)
        }}
    const savefun=()=>{
dispatch(userDataAction(userdetail))
    }
    const handleInput=(e)=>{
        const name=e.target.name
        const value=e.target.value.trim()
        setuserdetail({...userdetail,[name]:value})
    }
    console.log(Alldata.Data)
  return (<>
  <Navbar/>
    <div className="MainpageWrapper">
    <div className="innerwrapper">
<div className="formwrapper">
    <div className="upperform">
    {step===1?<PersonalInfo userdetail={userdetail} setuserdetail={setuserdetail} handleInput={handleInput}
    />:step===2?<FavouriteBooks userdetail={userdetail} setuserdetail={setuserdetail} handleInput={handleInput}/>:step===3?<Author userdetail={userdetail} setuserdetail={setuserdetail}handleInput={handleInput} />:<Alluserdetail userdetail={userdetail} setuserdetail={setuserdetail} handleInput={handleInput}/>}
    </div>
    <div className="bottomform">
    <div className="pagecount">
        <ul className="countlist">
            <li className={step===1?"active":null} onClick={()=>{setstep(1)}}>1</li>
            <li className={step===2?"active":null} onClick={()=>{setstep(2)}}>2</li>
            <li className={step===3?"active":null} onClick={()=>{setstep(3)}}>3</li>
            <li className={step===4?"active":null} onClick={()=>{setstep(4)}}>4</li>
        </ul>
    </div>
    <div className="bottombutton">
        <button className="prev btn" disabledonClick={Prevfun}>Previous</button>
        <button className="next btn" onClick={step===4?savefun:Nextfun}>{step===4?"Confirm & Save":"Next"}</button>
    </div>
    </div>
</div>
    </div>
    </div>
  </>

  )
}
